/**
 * Returns a boolean telling, if the given value is between `min` and `max`
 * or not.
 *
 * @example
 * ```js
 * const { is } = require('indicative')
 *
 * if (is.between(10, [5, 20])) {
 * }
 * ```
 */
exports.between = (input, [min, max]) => {
    return (input > min) && (input < max);
};
