/**
 * Returns a boolean telling if value is a valid JSON string
 * or not.
 *
 * @example
 * ```js
 * const { is } = require('indicative')
 *
 * if (is.json("{ \"hello\": \"world\" }")) {
 * }
 * ```
 */
exports.json = (input) => {
    if (typeof (input) !== 'string') {
        return false;
    }
    try {
        return !!JSON.parse(input);
    }
    catch (e) {
        return false;
    }
};
